import React from 'react'
import CardContainer from './CardContainer'

const API_KEY = 'c50f46cf-8ed0-402c-a5be-d41c7f559deb'
const url = 'https://api.thecatapi.com/v1/images/search'

class Cats extends React.Component {
    constructor(){
        super()
        this.state = {
            photos:[]
        }
    }

    componentDidMount(e){
        e.preventDefault()
        // ?=limit=10 establece que se recolecten 10 imagenes de la api
        fetch(url,{
            headers: {
                'x-api-key': API_KEY
            }
        })
        .then(response => {
            console.log(this.state.photos)
            if(!response.ok){
                throw Error("No cats were be found :^(")
            }
            return response.json()
            }
        )
        .then(allData => {
            this.setState({
                photos: allData
            })
            console.log(this.state.photos)
        })
        .catch(error => {
            throw Error(error.message)
        })

    }

    render(){
        return(
        <div>
            <p>{JSON.stringify(this.state.photos)}</p>
              <CardContainer photos={this.state.photos}/>
              <CardContainer>
                  {this.state.photos > 0 && this.state.photos.map(photo =>{
                      <p key={photo.id}>{photo}</p>
                  })}
              </CardContainer>
        </div>
        )
    }
}

export default Cats
