import React from 'react'
import CardContainer from './CardContainer'

const apiKey = 'c50f46cf-8ed0-402c-a5be-d41c7f559deb'

class Cats extends React.Component {
    constructor(){
        super()
        this.state = {
            photos:[]
        }
    }

    componentDidMount(){
        // ?=limit=10 establece que se recolecten 10 imagenes de la api
        fetch('https://api.thecatapi.com/v1/images/search')
        .then(response => {
            console.log(this.state.photos)
            if(!response.ok){
                throw Error("No cats were be found :^(")
            }
            return response.json()
            }
        )
        .then(allData => {
            this.setState({
                photos: allData
            })
            console.log(this.state.photos)
        })
        .catch(error => {
            throw Error(error.message)
        })

    }

    render(){
        return(
        <div>
            <p>{JSON.stringify(this.state.photos)}</p>
              <CardContainer photos={this.state.photos}/>
        </div>
        )
    }
}

export default Cats
